INSERT INTO `profilia` (`CODPROD`, `DESCRICAO`, `CODGRU`, `PRVENDA`, `UNIDADE`, `NUMFABRICANTE`, `OBS`) VALUES (1, 'SPOT LED 5W QUADRADO 3.000K AMARELO', 52, 12.900, 'UN', '7898586139428', 'O Spot LED 5W Quadrado de cor amarela, com temperatura de cor de 3.000K, oferece uma iluminação suave e aconchegante, perfeita para criar ambientes relaxantes em sua casa ou escritório. Seu design moderno e compacto permite uma instalação fácil em qualquer espaço, enquanto a tecnologia LED garante alta eficiência e durabilidade, reduzindo o consumo de energia. Ideal para destacar decorações e áreas específicas com elegância e eficiência.');
INSERT INTO `profilia` (`CODPROD`, `DESCRICAO`, `CODGRU`, `PRVENDA`, `UNIDADE`, `NUMFABRICANTE`, `OBS`) VALUES (2, 'SPOT LED 5W QUADRADO 6.500K BRANCO', 52, 12.900, 'UN', '7898586134560', 'O Spot LED 5W Quadrado na cor branca, com temperatura de cor de 6.500K, proporciona uma iluminação brilhante e energizante, ideal para ambientes que requerem clareza e atenção aos detalhes. Seu design quadrado e contemporâneo encaixa-se perfeitamente em escritórios, cozinhas ou qualquer espaço que beneficie de luz mais fria. A tecnologia LED assegura eficiência energética e uma longa vida útil, minimizando a necessidade de substituições frequentes. Perfeito para uma iluminação moderna e eficaz.');
