INSERT INTO `profilia` (`CODPROD`, `DESCRICAO`, `CODGRU`, `PRVENDA`, `UNIDADE`, `NUMFABRICANTE`, `OBS`) VALUES (10, 'ADESIVO INSTANTANEO 100G - RENDBOND', 53, 19.900, 'UN', '7898590650117', 'O Adesivo Instantâneo RendBond de 100g é a solução perfeita para colagens rápidas e eficazes. Sua fórmula de alta performance garante uma aderência forte e duradoura em uma ampla gama de materiais, incluindo madeira, metal, plástico e borracha. Fácil de aplicar, este adesivo é ideal para uso tanto em reparos domésticos quanto em aplicações profissionais, oferecendo resultados imediatos com secagem rápida. Com o RendBond, você tem a confiança de um trabalho bem feito, sem a espera.');
